extend type Query {
  isLoggedIn: Boolean!
  cartItems: [ID!]!
}

extend type Launch {
  isInCart: Boolean!
}

extend type Mutation {
  addOrRemoveFromCart(id: ID!): [Launch]
}

mutation BookTrips($launchIds: [ID]!) {
  bookTrips(launchIds: $launchIds) {
    success
    message
    launches {
      id
      isBooked
    }
  }
}

mutation cancel($launchId: ID!) {
  cancelTrip(launchId: $launchId) {
    success
    message
    launches {
      id
      isBooked
    }
  }
}
